class Solution {
public:
    vector<vector<int>> generateMatrix(int n) 
    {
        if(!n)
            return {};
        int rowStart = 0, rowEnd = n - 1, colStart = 0, colEnd = n - 1;
        int count = 1;
        vector<vector<int>> result(n, vector<int>(n));
        while(count <= n * n)
        {
            for(int i = colStart; i <= colEnd && count <= n * n; result[rowStart][i++] = count++);
            for(int i = ++rowStart; i <= rowEnd && count <=  n * n; result[i++][colEnd] = count++);
            for(int i = --colEnd; i >= colStart && count <=  n * n; result[rowEnd][i--] = count++);
            for(int i = --rowEnd; i >= rowStart && count <= n * n; result[i--][colStart] = count++);
            ++colStart;
        }
        return result;
    }
};
