class Solution {
public:
    vector<vector<int>> generateMatrix(int n) {
        int k = 0;                   //number of steps
        vector<vector<int>> res;     //to store the matrix

        //maybe you can find some better way to initialize the matrix array, but I decided to fill it with zeros))
        for(int i = 0; i < n; i++){
            vector<int> v;
            for(int j = 0; j < n; j++){
                v.push_back(0);
            }
            res.push_back(v);
        }

        //these variables will be used as pointers
        int r = n - 1;      //right bound 
        int l = 0;          //left bound
        int u = 0;          //up bound
        int d = n - 1;      //down bound
        int i = 0;          //vertical position
        int j = 0;          //horizontal position

        while(k++ < n * n){       //we should make n*n steps to fill all the matrix
            res[i][j] = k;

            if(i == u && j < r) {    //move right
                j++;
                if(j == r) u++;      //if the bound is reached, shift the bound
            }
            else if(i < d && j == r) {       //move down
                i++;
                if(i == d) r--;
            }
            else if(i == d && j > l) {     //move left
                j--;
                if(j == l) d--;
            }
            else if(i > u && j == l) {     //move right
                i--;
                if(i == u) l++;
            }
        }
        return res;
    }
};
