public class Solution {
    public int[][] GenerateMatrix(int n) 
    {
        int[][] matrix = new int[n][];

        for (int i = 0; i < n; i++)
        {
            matrix[i] = Enumerable.Repeat(0, n).ToArray();
        }

        int[] directionX = new int[] { 0, 1, 0, -1 };
        int[] directionY = new int[] { 1, 0, -1, 0 };
        int x = 0;
        int y = 0;
        int direction = 0;

        for (int i = 1; i <= n * n; i++)
        {
            matrix[x][y] = i;

            //do not need this on the last step
            if (i != n * n)
            {
                x += directionX[direction];
                y += directionY[direction];

                //if we are out of bounds or on the already visited cell then change the direction
                if (x < 0 || y < 0 || x > n-1 || y > n-1 || matrix[x][y] != 0)
                {
                    x -= directionX[direction];
                    y -= directionY[direction];

                    direction = (direction + 1) % 4;

                    x += directionX[direction];
                    y += directionY[direction];
                }
            }
        }

        return matrix;
    }
}
