            if(i>num)break;   
            for(int j=lastRow-1;j>firstRow;j--)
            {
                v[j][firstColumn]=i;
                i++;  
            }
            if(i>num)break;
            firstRow++;
            firstColumn++;
            lastRow--;
            lastColumn--;
        }
        return v;
    }
};
