public class Solution 
{
    public int[][] GenerateMatrix(int n) 
    {
        int[][] mat = new int[n][];
        for (int i = 0; i < n; i++)
            mat[i] = new int[n];

        (int top, int bottom, int left, int right) = (0, n - 1, 0, n - 1);

        int val = 1;
        while (true) 
        {
            //left to right, colms are changing
            for (int i = left; i <= right; i++)
                mat[top][i] = val++;

            top++; //row covered, bring it down

            if (top > bottom)
                break;

            //top to bottom, rows are changing
            for (int i = top; i <= bottom; i++)
                mat[i][right] = val++;

            right--; //colm covered, shrink the column window

            if (left > right)
                break;

            //right to left, colms are changing
            for (int i = right; i >= left; i--)
                mat[bottom][i] = val++;

            bottom--; //row covered, shrink the row window

            //bottom to top, rows are changing
            for (int i = bottom; i >= top; i--)
                mat[i][left] = val++;

            left++; //col covered, shrink the colm window

        }

        return mat;
    }
}
