public class Solution 
{
    private int count = 0;

    public int[][] GenerateMatrix(int n) 
    {
        int[][] mat = new int[n][];

        for(int i = 0; i < n; i++)
            mat[i] = new int[n];

        DFS(mat, 0, 0, n, Direction.RIGHT);

        return mat;
    }

    private void DFS(int[][] mat, int r, int c, int n, Direction direction)
    {
        if(r < 0 || c < 0 || r > n - 1 || c > n - 1 || mat[r][c] > 0)
            return;

        mat[r][c] = ++count;

        switch(direction)
        {
            case Direction.RIGHT:
                DFS(mat, r, c + 1, n, Direction.RIGHT);
                DFS(mat, r + 1, c, n, Direction.DOWN);
                break;
            case Direction.DOWN:
                DFS(mat, r + 1, c, n, Direction.DOWN);
                DFS(mat, r, c - 1, n, Direction.LEFT);
                break;
            case Direction.LEFT:
                DFS(mat, r, c - 1, n, Direction.LEFT);
