class Solution {
public:
    vector<vector<int>> generateMatrix(int n) {

        vector<vector<int>> aResult(n, vector<int>(n));

        int _U = 0, _R = n - 1, _D = n - 1, _L = 0, x = 0, y = 0, VAL = 1;

        while (_L <= _R) {

            x = _L; y = _U;

            //Sweep right across the top.
            while (x <= _R) { aResult[y][x++] = VAL++; }
            _U += 1; y = _U;

            //Sweep down the right side.
            while (y <= _D) { aResult[y++][_R] = VAL++; }
            _R -= 1; x = _R;

            //Swipe left across the bottom.
            while (x >= _L) { aResult[_D][x--] = VAL++; }
            _D -= 1; y = _D;

            //Sweep up the left side.
            while (y >= _U) { aResult[y--][_L] = VAL++; }
            _L += 1;
        }

        return aResult;
    }
};
