class Solution {
    func generateMatrix(_ n: Int) -> [[Int]] {    
        var matrix = [[Int]](repeating: [Int](repeating: 0, count: n), count: n) 
        var top = 0, bottom = n - 1
        var left = 0, right = n - 1
        var num = 1
        while top <= bottom && left <= right {
            for i in stride(from: left, to: right + 1, by: 1) {
                matrix[top][i] = num
                num += 1
            }
            top += 1

            for i in stride(from: top, to: bottom + 1, by: 1) {
                matrix[i][right] = num
                num += 1
            }
            right -= 1

            if top <= bottom {
                for i in stride(from: right, to: left - 1, by: -1) {
                    matrix[bottom][i] = num
                    num += 1
                }
            }
            bottom -= 1

            if left <= right {
                for i in stride(from: bottom, to: top - 1, by: -1) {
                    matrix[i][left] = num 
                    num += 1
                }
            }
            left += 1
        }
        return matrix
    }
}
