var generateMatrix = function(n) {
    // MATRIX[Y][X]
    //    ---------
    //Y0 | 1  2  3 |
    //Y1 | 4  5  6 |
    //Y2 | 7  8  9 |
    //    ---------
    //     X0 X1 X2

    let matrix=[],count=0,x=0,y=0;
    const maxElements=n*n;

    for(let i=0; i<n;i++)
    {
        matrix[i]= new Array()
    }

    while(maxElements>count)
    {
        for(let i=x; maxElements>count && i<n-x;i++)
        {
            count++;
            matrix[y][i]=count
            // console.log(1, matrix[y][i])
