```cpp
class Solution {
private:
    vector<int> getPrefix(const string& p) {
        int n = p.size();
        vector<int> prefix(n, 0);
        for (int i=1; i<n; i++) {
            int j = prefix[i-1];
            while (j and p[i] != p[j]) j = prefix[j-1];
            prefix[i] = p[i] == p[j] ? j+1 : prefix[i];
        }
        return prefix;
    }
public:
    bool repeatedSubstringPattern(const string& s) {
        auto prefix = getPrefix(s);
        int n = s.length(), last = prefix.back(), size = n - last;
        return last and !(last % size);
    }
};
```

