class Solution {
public:
    bool repeatedSubstringPattern(string s) {

        int n = s.length();

        for(int i=0;i<floor(n/2);i++){

            string str = s.substr(0,i+1);

            // now try to match this string with the remaining string
            int j = i+1;
            int itr = 0;
            bool flag=true;
            while(j<n){
                // trying to check each character of the string with pattern , if they are matching then the pattern string (str) is a valid answer till now
                if(str[itr]==s[j]){
                    itr++;
                }
                else{
                    flag=false;
                    break;
                }

                // setting the index of pattern string to 0 again since whole pattern string has matched once
                if(itr==str.length()){
                    itr=0;
                }
                j++;
            }

            /* for matching two conditions have to be true 
             1) flag should be true which means the string pattern has matched properly with respect to whole remaining string indicating the pattern when concatenated can form thw whole of the remaining string
             2) the length of whole string should be a multiple of pattern's length 
            */
            if(flag && n%str.length()==0){
                return true;
            }
        }


        return false;

    }
};

