public class Solution {
    public int MaxDepth(Node root) {

        if(root == null)
            return 0;

        Queue<Node> queue = new Queue<Node>();
        queue.Enqueue(root);
        int depth = 0;
        while(queue.Count > 0)
        {
            int size = queue.Count;
            for(int i = 0; i < size; i++)
            {
                Node curr = queue.Dequeue();           
                foreach(Node node in curr.children)
                    queue.Enqueue(node);
            }
            depth++;
        }

        return depth;
    }
}
