private int findMaxDepth(Node node, int currentDepth)
{
	int maxDepth = currentDepth;

	if(node == null)
	{
		return 0;
	}

	foreach(var child in node.children)
	{
		int depth = findMaxDepth(child, currentDepth + 1);
		maxDepth = Math.Max(maxDepth, depth);
	}

	return maxDepth;
}
