class Solution {
public:
    int maxDepth(Node* root) {
        if(!root) return 0;
        queue<Node*> q;
        q.push(root);
        q.push(nullptr);
        int count=1;
        while(root){
            Node* front = q.front();
            q.pop();
            if(front==nullptr){
                q.push(nullptr);
                if(q.size()==1) break;
                count++;
            }else{
                for(int i=0;i<front->children.size();i++){
                    q.push(front->children[i]);
                }
            }
        }
        return count;
    }
};
