public int maxDepth(Node root) {
        if(root==null) return 0;
        Queue<Node> queue = new LinkedList<Node>();
        int level=0;
        queue.offer(root);
        while(!queue.isEmpty()){
            int size=queue.size();
            level++;
            for(int i=0;i<size;i++){
                Node node=queue.poll();
                for(Node nextNode : node.children){
                    queue.offer(nextNode);
                }
            }
        }
        return level;
    }

    public int maxDepth(Node root) {
        return dfs(root);
    }
