var maxDepth = function(root) {
    if(!root) return 0;  // if root is null, depth is 0
    let depth = 1;  // root's depth is 1
    let max = 0;  // maximum depth of each children node

    for(let child of root.children) {
        max = Math.max(maxDepth(child), max)  // Calculate the maximum depth of each children node with recursion
											 // it means each children node to be root node in maxDepth() function recursion
    }
    depth += max;  // root node's depth is to be calculated root node's depth(1) plus maximum depth of each children node
    return depth;
};
