private void inPlace(int[][] A) {
		int col = 0;
		for (int i = 0; i<A.length ; i++) {
				for (int j=col ; j<A[0].length ; j++) {
						int temp = A[i][j];
						A[i][j] = A[j][i];
						A[j][i] = temp;
			}
				col++;
	}
}
