class Solution {
    public int[][] transpose(int[][] matrix) {
        int m = matrix.length;
        int n = matrix[0].length;
        int ans[][] = new int[n][m];
        int i=0,j=0,k=0;
        while(k<n){
            ans[k][i] = matrix[i][j];
            i++;
            if(i==m){
                i -= m;
                j++;
                k++;
            }
        }
        return ans;
    }
}
