class Solution {
    public int[][] transpose(int[][] matrix) {
        int[][] ans = new int[matrix[0].length][matrix.length];
        int m = 0;        //  for filling rows of ans matrix
        int n = 0;         // for filling columns of ans matrix
        for(int i=0;i<matrix[0].length;i++){
            for(int j=0;j<matrix.length;j++){
                ans[m][n] = matrix[j][i];             //  column-wise iterating through matrix (given) array

                n++;
            }
            m++;                          // normal looping in 'ans' array ( since m = row.. therefore increasing row )  and setting  n ( column) = 0 for next iteration in 'ans' array
            n = 0;
        }
        return ans;
    }

} 



