impl Solution {
    pub fn transpose(a: Vec<Vec<i32>>) -> Vec<Vec<i32>> {

        let n_row = a.len();
        let n_col = a.get(0).map(|row| row.len()).unwrap_or(0_usize);

        (0..n_col)
        .map(|c| {
            (0..n_row)
                .map(|r| a[r][c])
                .collect()
        })
        .collect()
    }
}
