class Solution {
    public int subarraysDivByK(int[] nums, int k) {
        // Create a HashMap to store the remainder count
        Map<Integer, Integer> remainderCount = new HashMap<>();
        // Initialize the count of 0 remainder to 1
        remainderCount.put(0, 1);
        // Initialize the sum to 0
        int sum = 0;
        // Initialize the result to 0
        int result = 0;
        // Iterate through the array
        for(int a : nums){
            // Add the current element to the sum
            sum = (sum + a) % k;
            // If the sum is negative, add k to make it positive
            if(sum < 0) sum += k;
            // If the remainder count map contains the current remainder, add the count to the result
            if(remainderCount.containsKey(sum)){
                result += remainderCount.get(sum);
            }
            // Add the current remainder to the remainder count map
            remainderCount.put(sum, remainderCount.getOrDefault(sum, 0) + 1);
        }
        // Return the result
        return result;
    }
}

