class Solution {
public:
    int subarraysDivByK(vector<int>& nums, int K) {
        int n = size(nums);
        if(n==0) return 0;

        int i = 0, total = 0;
        int currSum = 0;
        unordered_map<int, int> mp;
        mp[0] = 1;

        while(i<n)
        {
            currSum+= nums[i];
            int rem = currSum % K;
            if(rem<0) rem += K;
            if(mp.count(rem))
                total += mp[rem];
            mp[rem]++;
            i++;
        }
        return total;
    }
};
```

