	public int subarraysDivByK(int[] nums, int k) {
        int count = 0;
        int temp = 0;
        HashMap<Integer , Integer> map = new HashMap<>();
        HashMap<Integer , Integer> res = new HashMap<>();
        int sum = 0;
        for(int i : nums){
            sum += i;
            int rem = sum % k;
            while(rem < 0) {
                rem += k;
            }
            if(map.containsKey(rem)) {
                int tmp = map.get(rem) + 1;
                map.put(rem , tmp);
                temp = comb(tmp);
                res.put(rem , temp);
            } else map.put(rem , 1);
        }
        for(int i : res.keySet()){
            count += res.get(i);
        }
        if(map.containsKey(0)){
            count += map.get(0);
        }
        return count;
    }

    private int comb(int a){
        return ((a-1)*a)/2;
    }

