                res.put(rem , temp);
            } else map.put(rem , 1);
        }
        for(int i : res.keySet()){
            count += res.get(i);
        }
        if(map.containsKey(0)){
            count += map.get(0);
        }
        return count;
    }

    private int comb(int a){
        return ((a-1)*a)/2;
    }

