        public int subarraysDivByK(int[] A, int K) {
        int result = 0;
        int[] sum = new int[A.length+1];
        sum[0] = A[0];

        //prefix sum
        for (int i = 1;i<A.length;i++)
            sum[i] = sum[i-1] + A[i];

        // to make sure that the sum % k will leave us how much is needed to make it divisible by k
        // in between two reminders we are sure the sub array is completely divisible by K
        // ading K is to avoid overflow
        int[] count = new int[K];
        for (int i : sum)
            count[(i % K + K) % K]++;

        // for each of the available reminders we take NC2 to get the total
        for (int i : count)
            result += i * (i -1) / 2;
        return result;
    }
}
