class Solution(object):
    def subarraysDivByK(self, nums, k):
        """
        :type nums: List[int]
        :type k: int
        :rtype: int
        """
        s = defaultdict(int) # set()
        pref = 0
        count = 0
        s[0] = 1

        for i, n in enumerate(nums):
            pref += n
            target = pref%k

            if target < 0: target += k

            if target in s: count += s[target]

            s[target] += 1

        return count
