class Solution {
public:
    int subarraysDivByK(vector<int>& nums, int k) {

        int n = nums.size();

        unordered_map<int, int> hashmap; // prefix%k and it's frequency
        hashmap.insert({0, 1});

        int prefix = 0;

        int total = 0;

        for(int i=0; i<n; i++){
            prefix = (prefix + nums[i])%k;

            if(prefix < 0) prefix += k;

            if(hashmap.find(prefix%k) != hashmap.end()){
                total += hashmap[prefix%k];
            }

            hashmap[prefix%k]++;            
        }

        return total;
