class Solution {
public:
    int subarraysDivByK(vector<int>& nums, int k) {
    int count = 0; // variable to store the number of subarrays with sum divisible by k
    int prefix_sum = 0; // variable to store the prefix sum of subarrays
    unordered_map<int, int> remainder_count; // hashmap to store the count of remainders when prefix_sum is divided by k

    for (int i = 0; i < nums.size(); i++) {
        prefix_sum += nums[i]; // adding current element to prefix_sum
        int remainder = (prefix_sum % k + k) % k; // To handle negative numbers, we add k to make sure the remainder is positive
        if (remainder_count.find(remainder) != remainder_count.end()) {
            count += remainder_count[remainder]; // if remainder already exists, add the count of that remainder to count
        }
        if (remainder == 0) {
            count++; // if remainder is zero, increment count by 1
        }
        remainder_count[remainder]++; // increment count of remainder in remainder_count map
    }

    return count;
}

};
