class Solution {
    public int subarraysDivByK(int[] A, int K) {
        HashMap<Integer, Integer> map = new HashMap<>();
        int sum = 0;
        map.put(0,1);
        int rem = 0;
        int ans = 0;
        for(int i = 0; i<A.length;i++){
            sum += A[i];

            rem = sum % K;
            if(rem < 0) {
                rem = rem +K;
            }
            if(!map.containsKey(rem)) {
                map.put(rem, 1);
            }
            else {
                ans += map.get(rem);
                 map.put(rem, map.get(rem)+1);
            }
        }
        return ans;
    }
}
