        prefixSum += num;
        let mod = prefixSum % k;
        mod = mod < 0 ? mod + k : mod;
        let val = freqMap.get(mod) === undefined ? 0 : freqMap.get(mod);

        count += val;
        freqMap.set(mod, val + 1);
