		map[0] = 1;
		int runningSum = 0;
		int count = 0;

		for(int n : nums) {
			runningSum += n;
			int reminder = runningSum % k;
			if(reminder < 0) { reminder += k; }
			count += map[reminder];
			map[reminder]++;
		}
		return count;
