int subarraysDivByK(vector<int> &nums, int k)
{
    int n = nums.size();
    int count = 0;
    int s = 0;
    int r;

    unordered_map<int, int> dp;
    dp[0] = 1;

    for (int i = 0; i < n; ++i)
    {
        s += nums[i];
        r = s % k;

        if (r < 0)
            r += k;

        if (dp.count(r))
            ++dp[r];

        else
            dp[r] = 1;

        count += (dp[r] - 1);
    }

    return count;
}
