class Solution {
public:
    int subarraysDivByK(vector<int>& nums, int k) {
        int i = 0 , j = 0;
        int sum = 0;
        unordered_map<int ,int> remainderMap;
        int ans = 0;
        while(j < nums.size())
        {
            sum += nums[j];
            //why ? because in C++ or java -2%5 = -2 so make the remainders +ve by adding K
            remainderMap[((sum%k)+k)%k]++;

            j++;
        }

        for(auto x: remainderMap)
        {
            int rem = x.first;
            int count = x.second;

            if(rem == 0)
            {
                ans += count;
            }

            ans += (count*(count-1))/2;


        }
        return ans;
    }
};
