class Solution {
public:
    void duplicateZeros(vector<int>& arr) {
        const int VALUE_ZERO = 0;
        const int ARRAY_LENGTH = arr.size();

        int start = 0;
        int end = ARRAY_LENGTH-1;

        while(start < end){
            if(arr[start] == VALUE_ZERO)
                end--;
            start++;
        }

        for(int index=ARRAY_LENGTH-1; index>end; index--, end--){
            arr[index] = arr[end];

            if(arr[end] == VALUE_ZERO && start != end)
                arr[--index] = VALUE_ZERO;
        }
    }
};
