var duplicateZeros = function(arr) {
    let lastKeepIdx = arr.length-1
    let lastDuplicatedZero

    for (let i = 0; i <= lastKeepIdx; i++) {
        if (arr[i] === 0 && i !== lastKeepIdx) {
            lastKeepIdx--
            lastDuplicatedZero = i
        }
    }
    if (lastKeepIdx < arr.length-1) {
        for (let i = arr.length - 1; lastKeepIdx >= 0; i--) {
            arr[i] = arr[lastKeepIdx]
            if (arr[lastKeepIdx] === 0 && lastKeepIdx <= lastDuplicatedZero) {
                arr[i-1] = 0
                i--
            }
            lastKeepIdx--
        }
    }
};
