class Solution {
    public void duplicateZeros(int[] arr) {
        int zeroCount = 0;
        int newArraySize = 0;
        int iterator = 0;

        // count how many zeros should we duplicate
        // when 'newArraySize' is the same as `arr.length` we stop our search because future zeroes will be out the array range
        while(iterator < arr.length && newArraySize < arr.length) { 
            int val = arr[iterator];

            if(val == 0) {
                zeroCount++;
                newArraySize += 2; // 2 because we should add 2 zeroes to the result array
            } else {
                newArraySize++;
            }

            iterator++;
        }

        // two pointers which we will use to copy 
        // pointer when the first array should be ended after we add necessary zeroes
        int copyFrom = iterator - 1; // "-1" because we increased 'iterator' at the last step on our previous loop
        // where we will copy
        int copyTo = arr.length - 1;

        // if the last number is zero and if we copy it our new array will be longer than original one
        boolean isTrailingZero = newArraySize > arr.length;

        // go from the tail of array and move numbers
        while(copyFrom >= 0) {
            int val = arr[copyFrom];

            // if this numer is zero
            if(val == 0) {
                if(isTrailingZero && copyTo == arr.length - 1) {
                    // if it's the last number is zero we shouldn't add two zeroes to the result array
                    arr[copyTo] = val;
                } else {
                    // add two zeroes in a row 
                    arr[copyTo] = val;
                    copyTo--;
                    arr[copyTo] = val;
                }
            } else {
                // just copy the number
                arr[copyTo] = val;
            }

            // move pointers
            copyFrom--;
            copyTo--;
        }
    }
}
