// Solution (using swap())
class Solution {
public:
    void duplicateZeros(vector<int>& nums) {
        int count=0, n=nums.size();
        for(int i=0; i<n; i++)
        {
            if(nums[i]==0 && (i+1)<n){
                for(int j=n-1; j>i+1; j--)
                    swap(nums[j], nums[j-1]);
                nums[++i]=0;
            }
        }
    }
};

// Solution 2 (Using insert and resize)
class Solution {
public:
    void duplicateZeros(vector<int>& arr) {
        int i=0, n=arr.size();
        while(i<n)
        {
            if(arr[i]==0)
            {
                arr.insert(arr.begin()+i, 0);
                i += 2;
            }
            else
                i++;
        }
        arr.resize(n);
    }
};
