class Solution {
public:
    void duplicateZeros(vector<int>& arr) {
        int n = arr.size();
        int cnt = 0, idx;
        for(idx=0;idx<n; idx++ ){
            cnt++;
            if(!arr[idx]) cnt++;
            if(cnt>=n) break;
        }
        int pos = n-1;
        if(cnt>n){
            arr[pos--] = 0;
            idx--;
        }
        while(idx>=0){
            if(arr[i]){
                arr[pos--] = arr[i];
            }
            else{
                arr[pos--] = 0;
                arr[pos--] = 0;
            }
            idx--;
        }
    }
};
