class Solution {
    public int maxArea(int[] arr) {
        final int N = arr.length;
        int max = 0 , test, i=0, j=N-1;
        while(j>i){
            test = arr[i] < arr[j] ? arr[i] : arr[j];
            test = (j - i) * test;
            if(max < test) max = test;
            if(arr[i] < arr[j]) i++ ;
            else j--;
        }
