class Solution {
    func maxArea(_ h: [Int]) -> Int {
        guard h.count > 2 else { return min(h[0], h[1]) }
        var val = 0, lhs = 0, rhs = h.count - 1
        while lhs < rhs {
            val = max(val, min(h[lhs], h[rhs]) * (rhs - lhs))
            h[lhs] < h[rhs] ? (lhs += 1) : (rhs -= 1)
        }
        return val
    }
}
