 class Solution {
    fun maxArea(heights: IntArray): Int {
        // sanity check
        if(heights.isEmpty()) return 0

        val size = heights.size
        var lo = 0
        var hi = size -1 
        var maxArea = 0

        while(lo < hi){
            val area = minOf(heights[lo], heights[hi]) * (hi - lo)
