                calc(
                    startIdx = startIdx + 1,
                    endIdx = endIdx,
                    max = max(max, height[startIdx] * (endIdx - startIdx))
                )
            else
                calc(
                    startIdx = startIdx,
                    endIdx = endIdx - 1,
                    max = max(max, height[endIdx] * (endIdx - startIdx))
                )

    return calc(startIdx = 0, endIdx = height.size - 1, max = 0)
}
