class Solution {
    public int maxArea(int[] height) {
        // Initialize two pointers: l starts at the leftmost element, and r starts at the rightmost element
        int l = 0;
        int r = height.length - 1;
        int ans = 0; // Initialize the variable to store the maximum area

        // Use a two-pointer approach to find the container with the maximum area
        while (l < r) {
            // Calculate the current area based on the smaller of the two heights and the distance between the pointers
            int currentArea = (r - l) * Math.min(height[r], height[l]);

            // Update the maximum area if the current area is larger
            ans = Math.max(ans, currentArea);

            // Move the pointer that points to the smaller height
            if (height[r] > height[l]) {
                l++; // Move the left pointer towards the right
            } else {
                r--; // Move the right pointer towards the left
            }
        }

        return ans; // Return the maximum area found
    }
}

