        while(s<e)
        {
            int area = min(height[s],height[e]) * (e-s);
            maxi = max(maxi,area);
            if(height[s]>height[e])
            {
                e--;    
            }
            else if(height[s]<=height[e])
            {
                s++;
            }
        }
        return maxi;
