class Solution {
    fun maxArea(heights: IntArray): Int {
        if(heights.isEmpty()) return 0

        val size = heights.size
        var l = 0
        var h = size -1 
        var max = 0

        while(l < h){
            val area = minOf(heights[l], heights[h]) * (h - l)
            max = maxOf(max, area)

            if(heights[l] < heights[h])
                ++l
            else --h
        }

        return max
    }
}}
```
