class Solution {
public:
    int maxArea(vector<int>& height) {

// simple two point approach question very easy     
        int n =height.size(); 

        if( n==2)
        {
             return min(height[0], height[1]); 
        }

int ans = -1,  i=0 , j=n-1;

while( i<j)
{
  int val = min(height[i] ,height[j])*(j-i);
    ans =max(ans, val);

    if( height[i]>height[j])
    {
         j--;
         continue;
    }


    if( height[i]<height[j])
    {
        i++;
        continue;

    }

     i++;
      j--;
}


 return ans;    
    }
};

