class Solution {
    public int maxArea(int[] height) {
        if(height.length==2)
        {
            return (Math.min(height[0], height[1]));
        }
        int r=0;
        int h=0;
        int w=0;
        int j=height.length-1;
        int c=1;
        while(j>=0){
        int h1=height[j];
            for(int i=0;i<j;i++)
            {   if(h<height[i] && h<height[j]){
                int h2=height[i];
                c=Math.min(h1, h2)*(j-i);                             
                if(c>r){
                    r=c;
                  w=j-i;
                  h=Math.min(h1, h2); }
                }//if
            }//for
            j--;
        }//while
        return r;
    }
}

