```C++ []
class Solution {
public:
    int romanToInt(string s) {
        unordered_map<char, int> map = {
            { 'I' , 1 }, { 'V' , 5 }, { 'X' , 10 },
            { 'L' , 50 }, { 'C' , 100 }, { 'D' , 500 },
            { 'M' , 1000 }
        };

        int sum = map[s.back()];  // Initialize the sum with the value of the last character

        // Iterate through the string in reverse order, starting from the second-to-last character
        for (int i = s.length() - 2; i >= 0; --i) {
            // If the current character has a smaller value than the next character, subtract it from the sum
            if (map[s[i]] < map[s[i + 1]])
                sum -= map[s[i]];
            // Otherwise, add it to the sum
            else
                sum += map[s[i]];
        }

        return sum;  // Return the final sum
    }
};

