    public:

    int romanToInt(std::string s) 
    {
        int res = 0;
        int dim = s.size();

        for(int i = 0; i < dim; i++)
        {
            if(i < (dim - 1) && 
               fun(s[i]) < fun(s[i+1]))
            {
                res -= fun(s[i]);
            }
            else
            {
                res += fun(s[i]);
            }
        }

        return res;
    }
};

