  let sum = 0;

  for (let i = 0; i < s.length; i += 1) {
    const curValue = map.get(s[i]);
    const nextValue = map.get(s[i + 1]);

    if (nextValue > curValue) {
      sum = sum + nextValue - curValue;
      i += 1;
    } else {
      sum += curValue;
    }
  }

  return sum;
};
